import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Masonry from 'react-masonry-css';
import { Card, CardMedia, Box, Skeleton } from '@mui/material';
import { Link } from 'react-router-dom';
import LazyLoad from 'react-lazyload';

const breakpointColumnsObj = {
  default: 3,
  1100: 2,
  700: 1
};

const HomePage = () => {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const apiKey = process.env.REACT_APP_UNSPLASH_KEY;
        const res = await axios.get(`https://api.unsplash.com/photos?per_page=12&client_id=${apiKey}`);
        setPhotos(res.data);
      } catch (err) {
        console.error("Fotoğraflar yüklenirken hata oluştu:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPhotos();
  }, []);

  return (
    <Box sx={{ mt: 3 }}>
      {loading ? (
        <Masonry
          breakpointCols={breakpointColumnsObj}
          className="my-masonry-grid"
          columnClassName="my-masonry-grid_column"
        >
          {[...Array(12)].map((_, i) => (
            <Skeleton key={i} variant="rectangular" height={220} sx={{ borderRadius: 2, mb: 2 }} />
          ))}
        </Masonry>
      ) : (
        <Masonry
          breakpointCols={breakpointColumnsObj}
          className="my-masonry-grid"
          columnClassName="my-masonry-grid_column"
        >
          {photos.map(photo => (
            <Card
              key={photo.id}
              component={Link}
              to={`/photo/${photo.id}`}
              sx={{
                textDecoration: 'none',
                borderRadius: 2,
                overflow: 'hidden',
                position: 'relative',
                mb: 2,
                transition: 'transform 0.3s, box-shadow 0.3s',
                '&:hover': {
                  transform: 'scale(1.05)',
                  boxShadow: '0 20px 50px rgba(0,0,0,0.3)',
                  '& .overlay': { opacity: 1 },
                },
              }}
            >
              <LazyLoad height={220} offset={100} placeholder={<Skeleton variant="rectangular" height={220} sx={{ borderRadius: 2 }} />}>
                <CardMedia
                  component="img"
                  image={photo.urls?.regular}
                  alt={photo.alt_description}
                  sx={{ height: 220, objectFit: 'cover' }}
                />
              </LazyLoad>
              <Box
                className="overlay"
                sx={{
                  position: 'absolute',
                  bottom: 0,
                  width: '100%',
                  bgcolor: 'rgba(0,0,0,0.5)',
                  color: 'white',
                  p: 1,
                  opacity: 0,
                  transition: 'opacity 0.3s',
                }}
              >
                {photo.alt_description || 'Açıklama yok'}
              </Box>
            </Card>
          ))}
        </Masonry>
      )}
    </Box>
  );
};

export default HomePage;
