import React, { useState, useEffect, useCallback } from 'react';
import { searchPhotos } from './api';
import { useParams, Link } from 'react-router-dom';
import { Grid, Card, CardMedia, CardContent, Typography, Skeleton, Alert, Container, Box, Button, capitalize } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';

function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  const loadPhotos = useCallback(async () => {
    try {
      setError(null);
      setLoading(true);
      const response = await searchPhotos(category);
      setPhotos(response.data.results);
    } catch (error) {
      setError("Kategori fotoğrafları yüklenirken bir hata oluştu. Lütfen daha sonra tekrar deneyin.");
      console.error("Kategori fotoğrafları yüklenirken hata oluştu:", error);
    } finally {
      setLoading(false);
    }
  }, [category]);

  useEffect(() => {
    loadPhotos();
  }, [loadPhotos, refreshKey]);

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <CircularProgress />
      </div>
    );
  }

  if (error) {
    return <Alert severity="error" sx={{ margin: 2 }}>{error}</Alert>;
  }

  return (
    <Grid container spacing={2} sx={{ padding: 2 }}>
      {photos.map(photo => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
          <Card>
            <Link to={`/photo/${photo.id}`}>
              <CardMedia
                component="img"
                height="200"
                image={photo.urls.small}
                alt={photo.alt_description}
              />
            </Link>
            <CardContent>
              <Typography variant="body2" color="text.secondary">
                {photo.alt_description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default CategoryPage;
