import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchPhotoById } from './api';
import { Typography, Box } from '@mui/material';

export default function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPhotoById(id)
      .then(res => setPhoto(res.data))
      .catch(() => setError('Fotoğraf yüklenirken hata oluştu'));
  }, [id]);

  if (error) return <Typography color="error">{error}</Typography>;
  if (!photo) return <Typography>Yükleniyor...</Typography>;

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
      <Box
        component="img"
        src={photo.urls?.regular}
        alt={photo.alt_description || 'Fotoğraf'}
        sx={{ maxWidth: '100%', borderRadius: 2, boxShadow: 3 }}
      />
      <Typography variant="h6">{photo.alt_description || 'Açıklama yok'}</Typography>
      <Typography variant="body2" color="textSecondary">By: {photo.user?.name}</Typography>
    </Box>
  );
}
