import React, { useState, useEffect } from 'react';
import { fetchRandomPhotos } from './api';
import { Link } from 'react-router-dom';
import { Grid, Card, CardMedia, CardContent, Typography, CircularProgress, Alert } from '@mui/material';

function HomePage() {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadPhotos = async () => {
      try {
        setError(null);
        setLoading(true);
        const response = await fetchRandomPhotos();
        setPhotos(response.data);
      } catch (error) {
        setError("Fotoğraflar yüklenirken bir hata oluştu. Lütfen daha sonra tekrar deneyin.");
        console.error("Fotoğraflar yüklenirken hata oluştu:", error);
      } finally {
        setLoading(false);
      }
    };

    loadPhotos();
  }, []);

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <CircularProgress />
      </div>
    );
  }

  if (error) {
    return <Alert severity="error" sx={{ margin: 2 }}>{error}</Alert>;
  }

  return (
    <Grid container spacing={2} sx={{ padding: 2 }}>
      {photos.map(photo => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
          <Card>
            <Link to={`/photo/${photo.id}`}>
              <CardMedia
                component="img"
                height="200"
                image={photo.urls.small}
                alt={photo.alt_description}
              />
            </Link>
            <CardContent>
              <Typography variant="body2" color="text.secondary">
                {photo.alt_description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default HomePage;
