import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get(`https://api.unsplash.com/search/photos?client_id=YOUR_UNSPLASH_API_KEY&query=${category}&count=10`);
        setPhotos(response.data.results);
      } catch (error) {
        console.error("Kategori fotoğrafları yüklenirken hata oluştu:", error);
      }
    };

    fetchPhotos();
  }, [category]);

  return (
    <div>
      <h1>{category} Fotoğrafları</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {photos.map(photo => (
          <div key={photo.id} style={{ margin: '10px', width: '300px' }}>
            <Link to={`/photo/${photo.id}`}>
              <img src={photo.urls.small} alt={photo.alt_description} style={{ width: '100%', height: 'auto' }} />
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CategoryPage;
