import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { Grid, Card, CardMedia, CardContent, Typography } from '@mui/material';

function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get(`https://api.unsplash.com/search/photos?client_id=zRh08IBhgdZXkuKimuhFfYrkKHau3XMowvpkhN_2spY&query=${category}&count=10`);
        setPhotos(response.data.results);
      } catch (error) {
        console.error("Kategori fotoğrafları yüklenirken hata oluştu:", error);
      }
    };

    fetchPhotos();
  }, [category]);

  return (
    <Grid container spacing={2} sx={{ padding: 2 }}>
      {photos.map(photo => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
          <Card>
            <Link to={`/photo/${photo.id}`}>
              <CardMedia
                component="img"
                height="200"
                image={photo.urls.small}
                alt={photo.alt_description}
              />
            </Link>
            <CardContent>
              <Typography variant="body2" color="text.secondary">
                {photo.alt_description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default CategoryPage;
