// src/CategoryPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Box, Grid, Card, CardMedia, CardActionArea, CardContent, Typography, Button } from '@mui/material';
import axios from 'axios';
import Lightbox from 'yet-another-react-lightbox';
import 'yet-another-react-lightbox/styles.css';
import Thumbnails from 'yet-another-react-lightbox/plugins/thumbnails';
import 'yet-another-react-lightbox/plugins/thumbnails.css';

function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);
  const [page, setPage] = useState(1);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const navigate = useNavigate();
  const apiKey = process.env.REACT_APP_UNSPLASH_KEY;

  const fetchCategoryPhotos = async (pageNum = 1) => {
    try {
      const response = await axios.get('https://api.unsplash.com/search/photos', {
        params: { query: category, per_page: 12, page: pageNum },
        headers: { Authorization: `Client-ID ${apiKey}` },
      });
      setPhotos(prev => [...prev, ...response.data.results]);
    } catch (error) {
      console.error('Kategori fotoğrafları yüklenirken hata oluştu:', error);
    }
  };

  useEffect(() => { 
    setPhotos([]); // kategori değiştiğinde resetle
    setPage(1); 
    fetchCategoryPhotos(1); 
  }, [category]);

  useEffect(() => { if(page > 1) fetchCategoryPhotos(page); }, [page]);

  const openLightbox = (index) => {
    setCurrentIndex(index);
    setLightboxOpen(true);
  };

  const goToDetail = (id) => navigate(`/photo/${id}`);
  const loadMore = () => setPage(prev => prev + 1);

  return (
    <Box>
      <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>"{category}" Kategorisi</Typography>
      <Grid container spacing={3}>
        {photos.map((photo, index) => (
          <Grid item key={photo.id} xs={12} sm={6} md={4} lg={3}>
            <Card sx={{ borderRadius: 3, boxShadow: '0 8px 16px rgba(0,0,0,0.1)' }}>
              <CardActionArea onClick={() => openLightbox(index)}>
                <CardMedia
                  component="img"
                  height="200"
                  image={photo.urls?.small || ''}
                  alt={photo.alt_description || 'Unsplash Photo'}
                  sx={{ transition: 'transform 0.3s', '&:hover': { transform: 'scale(1.05)' } }}
                />
              </CardActionArea>
              <CardContent sx={{ display: 'flex', justifyContent: 'center' }}>
                <Button variant="contained" color="primary" onClick={() => goToDetail(photo.id)}>
                  Detayları Gör
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {photos.length > 0 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <Button variant="outlined" onClick={loadMore} sx={{ borderRadius: 2 }}>
            Daha Fazla Göster
          </Button>
        </Box>
      )}

      {lightboxOpen && (
        <Lightbox
          open={lightboxOpen}
          close={() => setLightboxOpen(false)}
          slides={photos.map((p) => ({ src: p.urls?.regular }))}
          plugins={[Thumbnails]}
          index={currentIndex}
          on={{ slideChange: (newIndex) => setCurrentIndex(newIndex) }}
        />
      )}
    </Box>
  );
}

export default CategoryPage;
