import React, { useState, useEffect } from 'react';
import { fetchPhotoById } from './api';
import { useParams, Link } from 'react-router-dom';
import { Card, CardMedia, CardContent, Typography, Button, CircularProgress, Alert, Container, Box } from '@mui/material';

function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadPhoto = async () => {
      try {
        setError(null);
        setLoading(true);
        const response = await fetchPhotoById(id);
        setPhoto(response.data);
      } catch (error) {
        setError("Fotoğraf detayı yüklenirken bir hata oluştu.");
        console.error("Fotoğraf detayları yüklenirken hata oluştu:", error);
      } finally {
        setLoading(false);
      }
    };

    loadPhoto();
  }, [id]);

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <CircularProgress />
      </div>
    );
  }

  if (error) {
    return <Alert severity="error" sx={{ margin: 2 }}>{error}</Alert>;
  }

  if (!photo) {
    return null; // or a 'not found' message
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Card>
        <CardMedia
          component="img"
          height="500"
          image={photo.urls.regular}
          alt={photo.alt_description}
        />
        <CardContent>
          <Typography variant="h5" component="div" gutterBottom>
            {photo.alt_description || 'Açıklama Yok'}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            {photo.description || 'Detaylı açıklama bulunmuyor.'}
          </Typography>
          <Box sx={{ mt: 2, '& > :not(style)': { m: 1 } }}>
            <Button variant="contained" href={photo.urls.full} download>
              İndir
            </Button>
            <Button component={Link} to="/" variant="outlined">
              Geri Dön
            </Button>
          </Box>
        </CardContent>
      </Card>
    </Container>
  );
}

export default PhotoDetail;
