// src/HomePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Grid, Card, CardMedia, CardActionArea, CardContent, Typography, Button } from '@mui/material';
import axios from 'axios';
import Lightbox from 'yet-another-react-lightbox';
import 'yet-another-react-lightbox/styles.css';
import Thumbnails from 'yet-another-react-lightbox/plugins/thumbnails';
import 'yet-another-react-lightbox/plugins/thumbnails.css';

function HomePage() {
  const [photos, setPhotos] = useState([]);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const navigate = useNavigate();
  const apiKey = process.env.REACT_APP_UNSPLASH_KEY;

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get('https://api.unsplash.com/photos', {
          params: { per_page: 12 },
          headers: { Authorization: `Client-ID ${apiKey}` },
        });
        setPhotos(response.data);
      } catch (error) {
        console.error('Fotoğraflar yüklenirken hata oluştu:', error);
      }
    };
    fetchPhotos();
  }, [apiKey]);

  const openLightbox = (index) => {
    setCurrentIndex(index);
    setLightboxOpen(true);
  };

  const goToDetail = (id) => navigate(`/photo/${id}`);

  return (
    <Box>
      <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>Popüler Fotoğraflar</Typography>
      <Grid container spacing={3}>
        {photos.map((photo, index) => (
          <Grid item key={photo.id} xs={12} sm={6} md={4} lg={3}>
            <Card>
              <CardActionArea onClick={() => openLightbox(index)}>
                <CardMedia
                  component="img"
                  height="200"
                  image={photo.urls?.small || ''}
                  alt={photo.alt_description || 'Unsplash Photo'}
                />
              </CardActionArea>
              <CardContent sx={{ display: 'flex', justifyContent: 'center' }}>
                <Button variant="contained" color="primary" onClick={() => goToDetail(photo.id)}>Detayları Gör</Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {lightboxOpen && (
        <Lightbox
          open={lightboxOpen}
          close={() => setLightboxOpen(false)}
          slides={photos.map((p) => ({ src: p.urls?.regular }))}
          plugins={[Thumbnails]}
          index={currentIndex}
          on={{ slideChange: (newIndex) => setCurrentIndex(newIndex) }}
        />
      )}
    </Box>
  );
}

export default HomePage;
