import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { fetchRandomPhotos } from './api';
import { Box, Card, CardMedia, Typography, Chip } from '@mui/material';
import Masonry from 'react-masonry-css';

export default function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetchRandomPhotos()
      .then(res => setPhotos(res.data.filter(photo => photo.title.toLowerCase().includes(category.toLowerCase()))))
      .catch(err => console.error("Fotoğraflar yüklenirken hata:", err));
  }, [category]);

  const breakpointColumnsObj = {
    default: 5,
    1500: 4,
    1200: 3,
    900: 2,
    600: 1,
  };

  return (
    <Box sx={{ mt: 4, px: { xs: 2, md: 8 }, maxWidth: 1600, mx: 'auto' }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        "{category}" Kategorisi
      </Typography>

      <Masonry
        breakpointCols={breakpointColumnsObj}
        className="my-masonry-grid"
        columnClassName="my-masonry-grid_column"
      >
        {photos.map(photo => (
          <Card
            key={photo.id}
            component={Link}
            to={`/photo/${photo.id}`}
            sx={{
              textDecoration: 'none',
              borderRadius: 3,
              overflow: 'hidden',
              position: 'relative',
              mb: 3,
              transition: 'transform 0.3s, box-shadow 0.3s',
              maxWidth: 350,
              margin: 'auto',
              '&:hover': {
                transform: 'scale(1.08)',
                boxShadow: '0 25px 60px rgba(0,0,0,0.35)',
                '& .overlay': { opacity: 1 },
              },
            }}
          >
            <CardMedia
              component="img"
              image={photo.thumbnailUrl || photo.url}
              alt={photo.title}
            />
            <Box
              className="overlay"
              sx={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                width: '100%',
                p: 1,
                bgcolor: 'rgba(0,0,0,0.5)',
                color: 'white',
                opacity: 0,
                transition: 'opacity 0.3s',
              }}
            >
              <Typography variant="subtitle2" noWrap>
                {photo.title}
              </Typography>
              <Chip label={category} size="small" sx={{ mt: 0.5, bgcolor: 'secondary.main', color: 'white' }} />
            </Box>
          </Card>
        ))}
      </Masonry>
    </Box>
  );
}
