import axios from 'axios';

const UNSPLASH_KEY = process.env.REACT_APP_UNSPLASH_KEY;

const apiClient = axios.create({
  baseURL: 'https://api.unsplash.com',
  headers: {
    Authorization: `Client-ID ${UNSPLASH_KEY}`,
  },
});

// Rasgele fotoğraflar
export const fetchRandomPhotos = () => {
  return apiClient.get('/photos', { params: { per_page: 12 } });
};

// Kategori/Arama ile fotoğraflar
export const searchPhotos = (query) => {
  return apiClient.get('/search/photos', { params: { query, per_page: 12 } });
};

// ID ile fotoğraf detay
export const fetchPhotoById = (id) => {
  return apiClient.get(`/photos/${id}`);
};
