import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { Card, CardMedia, CardContent, Typography, Button } from '@mui/material';

function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    const fetchPhoto = async () => {
      try {
        const response = await axios.get(`https://api.unsplash.com/photos/${id}?client_id=zRh08IBhgdZXkuKimuhFfYrkKHau3XMowvpkhN_2spY`);
        setPhoto(response.data);
      } catch (error) {
        console.error("Fotoğraf detayları yüklenirken hata oluştu:", error);
      }
    };

    fetchPhoto();
  }, [id]);

  if (!photo) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <Card>
      <CardMedia
        component="img"
        height="500"
        image={photo.urls.regular}
        alt={photo.alt_description}
      />
      <CardContent>
        <Typography variant="h5" component="div">
          {photo.alt_description}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {photo.description}
        </Typography>
        <Button variant="contained" href={photo.urls.full} download>
          İndir
        </Button>
        <Button component={Link} to="/">
          Geri Dön
        </Button>
      </CardContent>
    </Card>
  );
}

export default PhotoDetail;
