import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'https://api.unsplash.com',
  headers: {
    Authorization: `Client-ID DZy9NDBF7_-q5w3PLHznZlDp1ZQ7sCOng2Rjiwnyot0`,
  },
});

export const fetchRandomPhotos = (count = 12) => {
  return apiClient.get(`/photos/random?count=${count}`);
};

export const searchPhotos = (query, count = 12) => {
  return apiClient.get(`/search/photos?query=${query}&count=${count}`);
};

export const fetchPhotoById = (id) => {
  return apiClient.get(`/photos/${id}`);
};

export default apiClient;
