import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'https://jsonplaceholder.typicode.com',
});

export const fetchRandomPhotos = () => {
  return apiClient.get('/photos?_limit=12');
};

export const searchPhotos = (query) => {
  // Mock search with first 12 photos for simplicity (placeholder has no search)
  return apiClient.get('/photos?_limit=12');
};

export const fetchPhotoById = (id) => {
  return apiClient.get(`/photos/${id}`);
};
