import React, { useState, useEffect } from 'react';
import { fetchPhotoById } from './api';
import { useParams, Link } from 'react-router-dom';
import { Card, CardMedia, CardContent, Typography, Button, CircularProgress, Alert, Container, Box, Chip, Avatar, Divider } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import Skeleton from '@mui/material/Skeleton';
import FavoriteIcon from '@mui/icons-material/Favorite';


function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  const loadPhoto = async () => {
    try {
      setError(null);
      setLoading(true);
      const response = await fetchPhotoById(id);
      setPhoto(response.data);
    } catch (error) {
      setError("Fotoğraf detayı yüklenirken bir hata oluştu.");
      console.error("Fotoğraf detayları yüklenirken hata oluştu:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPhoto();
  }, [id, refreshKey]);

  if (error) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
          <Button onClick={loadPhoto} sx={{ ml: 2 }}>
            <RefreshIcon sx={{ mr: 1 }} /> Tekrar Dene
          </Button>
        </Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
        <Button component={Link} to="/" variant="outlined" sx={{ borderRadius: 2 }}>
          Ana Sayfa
        </Button>
        <Button variant="outlined" onClick={() => window.history.back()} sx={{ borderRadius: 2 }}>
          Geri
        </Button>
        <Button 
          variant="outlined" 
          startIcon={<RefreshIcon />}
          onClick={() => setRefreshKey(prev => prev + 1)}
          sx={{ borderRadius: 2 }}
        >
          Yenile
        </Button>
      </Box>
      {loading ? (
        <Card sx={{ maxWidth: 600 }}>
          <Skeleton variant="rectangular" height={500} />
          <CardContent>
            <Skeleton height={40} />
            <Skeleton height={20} sx={{ mt: 2 }} />
          </CardContent>
        </Card>
      ) : (
        photo && (
          <Card sx={{ maxWidth: 800 }}>
            <CardMedia
              component="img"
              height="500"
              image={photo.urls.regular}
              alt={photo.alt_description}
              sx={{ objectFit: 'cover' }}
            />
            <CardContent sx={{ p: 4 }}>
              <Typography variant="h4" fontWeight={700} gutterBottom mb={3}>
                {photo.alt_description || 'Başlıksız Fotoğraf'}
              </Typography>
              <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.6 }}>
                {photo.description || 'Bu fotoğraf hakkında detaylı açıklama mevcut değil.'}
              </Typography>
              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" fontWeight={600} gutterBottom>
                  Yazar
                </Typography>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Avatar 
                    src={photo.user.profile_image?.small || '/default-avatar.png'} 
                    alt={photo.user.name}
                    sx={{ width: 50, height: 50 }}
                  />
                  <Box>
                    <Typography variant="subtitle1" fontWeight={600}>
                      {photo.user.name}
                    </Typography>
                    <Button 
                      href={`https://unsplash.com/@${photo.user.username}`} 
                      target="_blank" 
                      size="small" 
                      variant="text"
                    >
                      Unsplash Profili
                    </Button>
                  </Box>
                </Box>
              </Box>
              <Divider sx={{ my: 2 }} />
              <Typography variant="h6" fontWeight={600} gutterBottom>
                İstatistikler
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                <Chip label={`${photo.likes || 0} Beğeni`} icon={<FavoriteIcon />} color="primary" />
                <Chip label={`${photo.views || 0} Görüntü`} color="secondary" />
                <Chip label={`${photo.downloads || 0} İndir`} color="success" />
              </Box>
              <Box sx={{ mt: 3 }}>
                <Button 
                  variant="contained" 
                  href={photo.urls.full} 
                  download={photo.id}
                  size="large"
                  sx={{ borderRadius: 2, mr: 2 }}
                >
                  Yüksek Çözünürlük İndir
                </Button>
              </Box>
            </CardContent>
          </Card>
        )
      )}
    </Container>
  );

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Card>
        <CardMedia
          component="img"
          height="500"
          image={photo.urls.regular}
          alt={photo.alt_description}
        />
        <CardContent>
          <Typography variant="h5" component="div" gutterBottom>
            {photo.alt_description || 'Açıklama Yok'}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            {photo.description || 'Detaylı açıklama bulunmuyor.'}
          </Typography>
          <Box sx={{ mt: 2, '& > :not(style)': { m: 1 } }}>
            <Button variant="contained" href={photo.urls.full} download>
              İndir
            </Button>
            <Button component={Link} to="/" variant="outlined">
              Geri Dön
            </Button>
          </Box>
        </CardContent>
      </Card>
    </Container>
  );
}

export default PhotoDetail;
