// src/HomePage.js
import React, { useState, useEffect } from 'react';
import { Box, Grid, Card, CardMedia, CardActionArea } from '@mui/material';
import axios from 'axios';
import Lightbox from 'yet-another-react-lightbox';
import 'yet-another-react-lightbox/styles.css';
import Thumbnails from 'yet-another-react-lightbox/plugins/thumbnails';
import 'yet-another-react-lightbox/plugins/thumbnails.css';

function HomePage() {
  const [photos, setPhotos] = useState([]);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Unsplash API key'in .env dosyasında REACT_APP_UNSPLASH_KEY olarak tanımlı olmalı
  const apiKey = process.env.REACT_APP_UNSPLASH_KEY;

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get('https://api.unsplash.com/photos', {
          params: { per_page: 12 },
          headers: { Authorization: `Client-ID ${apiKey}` },
        });
        setPhotos(response.data);
      } catch (error) {
        console.error('Fotoğraflar yüklenirken hata oluştu:', error);
      }
    };
    fetchPhotos();
  }, [apiKey]);

  const openLightbox = (index) => {
    setCurrentIndex(index);
    setLightboxOpen(true);
  };

  return (
    <Box sx={{ p: { xs: 2, md: 4 }, backgroundColor: '#f8fafc', minHeight: 'calc(100vh - 64px)' }}>
      <Grid container spacing={3}>
        {photos.map((photo, index) => (
          <Grid item key={photo.id} xs={12} sm={6} md={4} lg={3}>
            <Card sx={{ borderRadius: 3, boxShadow: '0 8px 16px rgba(0,0,0,0.1)' }}>
              <CardActionArea onClick={() => openLightbox(index)}>
                <CardMedia
                  component="img"
                  height="200"
                  image={photo.urls?.small || ''}
                  alt={photo.alt_description || 'Unsplash Photo'}
                  sx={{
                    transition: 'transform 0.3s',
                    '&:hover': { transform: 'scale(1.05)' },
                  }}
                />
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>

      {lightboxOpen && (
        <Lightbox
          open={lightboxOpen}
          close={() => setLightboxOpen(false)}
          slides={photos.map((p) => ({ src: p.urls?.regular }))}
          plugins={[Thumbnails]}
          index={currentIndex}
          on={{
            slideChange: (newIndex) => setCurrentIndex(newIndex),
          }}
        />
      )}
    </Box>
  );
}

export default HomePage;
