import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchPhotoById } from './api';
import { Typography, Box } from '@mui/material';

function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPhotoById(id)
      .then(res => setPhoto(res.data))
      .catch(err => setError('Fotoğraf yüklenirken hata oluştu'));
  }, [id]);

  if (error) return <Typography color="error">{error}</Typography>;
  if (!photo) return <Typography>Yükleniyor...</Typography>;

  return (
    <Box>
      <img src={photo.urls?.regular} alt={photo.alt_description || 'Fotoğraf'} style={{ width: '100%', borderRadius: 8 }} />
      <Typography variant="h6" mt={2}>{photo.alt_description || 'Açıklama yok'}</Typography>
      <Typography variant="body2">By: {photo.user?.name}</Typography>
    </Box>
  );
}

export default PhotoDetail;
