import React, { useEffect, useState } from 'react';
import { Grid, Card, CardMedia, CardContent, Typography } from '@mui/material';
import { fetchRandomPhotos } from './api';
import { Link } from 'react-router-dom';

function HomePage() {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetchRandomPhotos()
      .then(res => setPhotos(res.data))
      .catch(err => console.error('Fotoğraflar yüklenirken hata:', err));
  }, []);

  return (
    <Grid container spacing={3} sx={{ mt: 2 }}>
      {photos.map(photo => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
          <Card sx={{ borderRadius: 3, overflow: 'hidden', boxShadow: 3, transition: 'transform 0.2s', '&:hover': { transform: 'scale(1.03)' } }}>
            <Link to={`/photo/${photo.id}`} style={{ textDecoration: 'none' }}>
              <CardMedia
                component="img"
                height="200"
                image={photo?.urls?.small || 'https://via.placeholder.com/200'}
                alt={photo.alt_description || 'Fotoğraf'}
              />
              <CardContent>
                <Typography variant="body2" color="text.primary" noWrap>
                  {photo.alt_description || 'Fotoğraf'}
                </Typography>
              </CardContent>
            </Link>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default HomePage;
