import React, { useState, useEffect } from 'react';
import { Box, Grid, Card, CardMedia, Typography } from '@mui/material';
import axios from 'axios';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

function HomePage() {
  const [photos, setPhotos] = useState([]);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const res = await axios.get('https://api.unsplash.com/photos', {
          params: { per_page: 12 },
          headers: { Authorization: `Client-ID ${process.env.REACT_APP_UNSPLASH_KEY}` },
        });
        setPhotos(res.data);
      } catch (err) {
        console.error('Fotoğraflar yüklenirken hata oluştu:', err);
      }
    };
    fetchPhotos();
  }, []);

  return (
    <Box>
      <Grid container spacing={3}>
        {photos.map((photo, index) => (
          <Grid item xs={12} sm={6} md={4} key={photo.id}>
            <Card
              sx={{ position: 'relative', cursor: 'pointer', borderRadius: 3, overflow: 'hidden', transition: 'transform 0.3s, box-shadow 0.3s',
                '&:hover': { transform: 'scale(1.05)', boxShadow: '0 12px 40px rgba(0,0,0,0.2)' } }}
              onClick={() => { setCurrentPhotoIndex(index); setLightboxOpen(true); }}
            >
              <CardMedia
                component="img"
                height="220"
                image={photo.urls.small}
                alt={photo.alt_description || 'Fotoğraf'}
              />
              {/* Overlay */}
              <Box
                sx={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  width: '100%',
                  bgcolor: 'rgba(0,0,0,0.5)',
                  color: 'white',
                  p: 1.5,
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                }}
              >
                <Typography variant="subtitle2">{photo.alt_description?.slice(0, 20) || 'Fotoğraf'}</Typography>
                <Typography variant="caption">{photo.user.name}</Typography>
              </Box>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Lightbox */}
      {lightboxOpen && (
        <Lightbox
          mainSrc={photos[currentPhotoIndex].urls.full}
          nextSrc={photos[(currentPhotoIndex + 1) % photos.length].urls.full}
          prevSrc={photos[(currentPhotoIndex + photos.length - 1) % photos.length].urls.full}
          onCloseRequest={() => setLightboxOpen(false)}
          onMovePrevRequest={() =>
            setCurrentPhotoIndex((currentPhotoIndex + photos.length - 1) % photos.length)
          }
          onMoveNextRequest={() =>
            setCurrentPhotoIndex((currentPhotoIndex + 1) % photos.length)
          }
          imageCaption={photos[currentPhotoIndex].alt_description}
        />
      )}
    </Box>
  );
}

export default HomePage;
