import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PhotoGallery from './PhotoGallery';
import { Box, Typography } from '@mui/material';

const HomePage = () => {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get(
          `https://api.unsplash.com/photos?per_page=12&client_id=${process.env.REACT_APP_UNSPLASH_KEY}`
        );
        setPhotos(response.data);
      } catch (error) {
        console.error('Fotoğraflar yüklenirken hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPhotos();
  }, []);

  if (loading) return <Typography>Yükleniyor...</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>Popüler Fotoğraflar</Typography>
      <PhotoGallery photos={photos} />
    </Box>
  );
};

export default HomePage;
