import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function HomePage() {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get('https://api.unsplash.com/photos/random?client_id=YOUR_UNSPLASH_API_KEY&count=10');
        setPhotos(response.data);
      } catch (error) {
        console.error("Fotoğraflar yüklenirken hata oluştu:", error);
      }
    };

    fetchPhotos();
  }, []);

  return (
    <div>
      <h1>Rastgele Fotoğraflar</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {photos.map(photo => (
          <div key={photo.id} style={{ margin: '10px', width: '300px' }}>
            <Link to={`/photo/${photo.id}`}>
              <img src={photo.urls.small} alt={photo.alt_description} style={{ width: '100%', height: 'auto' }} />
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
