import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    const fetchPhoto = async () => {
      try {
        const response = await axios.get(`https://api.unsplash.com/photos/${id}?client_id=YOUR_UNSPLASH_API_KEY`);
        setPhoto(response.data);
      } catch (error) {
        console.error("Fotoğraf detayları yüklenirken hata oluştu:", error);
      }
    };

    fetchPhoto();
  }, [id]);

  if (!photo) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div>
      <h1>Fotoğraf Detayı</h1>
      <img src={photo.urls.regular} alt={photo.alt_description} style={{ width: '50%' }} />
      <p>{photo.description}</p>
      <a href={photo.urls.full} download>
        İndir
      </a>
    </div>
  );
}

export default PhotoDetail;
