import React, { useState, useEffect, useCallback } from 'react';
import { searchPhotos } from './api';
import { useParams, Link } from 'react-router-dom';
import { Grid, Card, CardMedia, CardContent, Typography, Skeleton, Alert, Container, Box, Button } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';

function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  const loadPhotos = useCallback(async () => {
    try {
      setError(null);
      setLoading(true);
      const response = await searchPhotos(category);
      setPhotos(response.data || []);
    } catch (error) {
      setError("Kategori fotoğrafları yüklenirken bir hata oluştu. Lütfen daha sonra tekrar deneyin.");
      console.error("Kategori fotoğrafları yüklenirken hata oluştu:", error);
      setPhotos([]);
    } finally {
      setLoading(false);
    }
  }, [category]);

  useEffect(() => {
    loadPhotos();
  }, [loadPhotos, refreshKey]);

  if (error) {
    return (
      <Container maxWidth="lg">
        <Alert
          severity="error"
          sx={{ mt: 4 }}
          action={
            <Button onClick={loadPhotos} size="small" sx={{ borderRadius: 1 }}>
              <RefreshIcon sx={{ mr: 1 }} />
              Tekrar Dene
            </Button>
          }
        >
          {error}
        </Alert>
      </Container>
    );
  }

  const categoryTitle = category ? (category.charAt(0).toUpperCase() + category.slice(1) + " Kategorisi") : "Kategori";

  return (
    <Container maxWidth="lg">
      <Box sx={{ 
        display: 'flex', 
        flexDirection: { xs: 'column', md: 'row' }, 
        justifyContent: { xs: 'center', md: 'space-between' }, 
        alignItems: { xs: 'center', md: 'center' }, 
        mb: 4, mt: 2, 
        gap: 2 
      }}>
        <Typography 
          variant={{ xs: 'h5', sm: 'h4' }} 
          component="h1" 
          fontWeight={700}
          sx={{ textAlign: { xs: 'center' }, flexShrink: 1 }}
        >
          {categoryTitle}
        </Typography>
        <Button
          variant="outlined"
          startIcon={<RefreshIcon />}
          onClick={() => setRefreshKey(prev => prev + 1)}
          sx={{ borderRadius: 2, flexShrink: 0, minWidth: { xs: 150 } }}
          fullWidth={{ xs: true, md: false }}
        >
          Yeni Fotoğraflar
        </Button>
      </Box>
      <Grid container spacing={3}>
        {loading ? 
          Array.from(new Array(12)).map((_, i) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={`skeleton-${i}`}>
              <Card sx={{ height: '100%' }}>
                <Skeleton variant="rectangular" height={200} sx={{ borderRadius: 1 }} />
                <CardContent sx={{ p: 2 }}>
                  <Skeleton sx={{ height: 20 }} />
                </CardContent>
              </Card>
            </Grid>
          )) 
          : photos.length === 0 ? (
            <Grid item xs={12}>
              <Box sx={{ textAlign: 'center', py: 8 }}>
                <Typography variant="h6" color="text.secondary">
                  Bu kategoride henüz fotoğraf bulunmuyor.
                </Typography>
              </Box>
            </Grid>
          ) : (
            photos.map(photo => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
                <Card sx={{ height: '100%' }}>
                  <Link to={`/photo/${photo.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                    <CardMedia
                      component="img"
                      height="200"
                    image={photo.download_url + '?w=300&h=200'}
                    alt={photo.alt_description || photo.description || 'Açıklama yok'}
                      sx={{ objectFit: 'cover' }}
                    />
                  </Link>
                  <CardContent sx={{ p: 2 }}>
                    <Typography variant="body2" color="text.secondary" sx={{ height: 40, overflow: 'hidden' }}>
                      {photo.alt_description || photo.description || 'Açıklama yok'}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))
          )
        }
      </Grid>
    </Container>
  );
}

export default CategoryPage;
