import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Card, CardMedia, CircularProgress } from '@mui/material';
import { fetchPhotoById } from './api';

function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getPhoto = async () => {
      try {
        const data = await fetchPhotoById(id);
        setPhoto(data);
      } catch (error) {
        console.error('Fotoğraf detayları yüklenirken hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };
    getPhoto();
  }, [id]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: 'calc(100vh - 64px)' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!photo) {
    return (
      <Box sx={{ p: 4 }}>
        <Typography variant="h6">Fotoğraf bulunamadı.</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ p: { xs: 2, md: 4 }, backgroundColor: '#f8fafc', minHeight: 'calc(100vh - 64px)' }}>
      <Card sx={{ maxWidth: '100%', borderRadius: 3, boxShadow: '0 8px 16px rgba(0,0,0,0.1)' }}>
        <CardMedia
          component="img"
          image={photo.urls?.regular || ''}
          alt={photo.alt_description || 'Unsplash Photo'}
          sx={{ maxHeight: '80vh', objectFit: 'contain' }}
        />
        <Box sx={{ p: 2 }}>
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            {photo.alt_description || 'Fotoğraf'}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Fotoğrafçı: {photo.user?.name || 'Bilinmiyor'}
          </Typography>
        </Box>
      </Card>
    </Box>
  );
}

export default PhotoDetail;
