import React, { useEffect, useState } from 'react';
import { fetchRandomPhotos } from './api';
import { ImageList, ImageListItem, Typography, Box } from '@mui/material';
import { Link } from 'react-router-dom';

export default function HomePage() {
  const [photos, setPhotos] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchRandomPhotos()
      .then(res => setPhotos(res.data))
      .catch(() => setError('Fotoğraflar yüklenirken hata oluştu'));
  }, []);

  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <Box>
      <ImageList variant="masonry" cols={{ xs: 1, sm: 2, md: 3 }} gap={16}>
        {photos.map(photo => (
          <ImageListItem key={photo.id} component={Link} to={`/photo/${photo.id}`} sx={{ textDecoration: 'none', borderRadius: 2, overflow: 'hidden' }}>
            <img
              src={`${photo.urls?.regular}?w=248&fit=crop&auto=format`}
              srcSet={`${photo.urls?.regular}?w=248&fit=crop&auto=format&dpr=2 2x`}
              alt={photo.alt_description || 'Fotoğraf'}
              loading="lazy"
              style={{ borderRadius: 8, width: '100%', display: 'block' }}
            />
          </ImageListItem>
        ))}
      </ImageList>
    </Box>
  );
}
