// src/PhotoDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Card, CardMedia, CardContent } from '@mui/material';
import axios from 'axios';

function PhotoDetail() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);

  const apiKey = process.env.REACT_APP_UNSPLASH_KEY;

  useEffect(() => {
    const fetchPhoto = async () => {
      try {
        const response = await axios.get(`https://api.unsplash.com/photos/${id}`, {
          headers: { Authorization: `Client-ID ${apiKey}` },
        });
        setPhoto(response.data);
      } catch (error) {
        console.error('Fotoğraf detayları yüklenirken hata oluştu:', error);
      }
    };
    fetchPhoto();
  }, [id, apiKey]);

  if (!photo) {
    return <Typography sx={{ p: 4 }}>Yükleniyor...</Typography>;
  }

  return (
    <Box sx={{ p: { xs: 2, md: 4 }, minHeight: 'calc(100vh - 64px)', backgroundColor: '#f8fafc' }}>
      <Card sx={{ borderRadius: 3, boxShadow: '0 12px 24px rgba(0,0,0,0.15)' }}>
        <CardMedia
          component="img"
          image={photo.urls?.regular || ''}
          alt={photo.alt_description || 'Unsplash Photo'}
          sx={{ borderTopLeftRadius: 12, borderTopRightRadius: 12 }}
        />
        <CardContent>
          <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
            {photo.description || photo.alt_description || 'Fotoğraf Detayı'}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Fotoğrafçı: {photo.user?.name || 'Bilinmiyor'}
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default PhotoDetail;
