import React, { useState, useEffect, useCallback } from 'react';
import { searchPhotos } from './api';
import { useParams, Link } from 'react-router-dom';
import { Grid, Card, CardMedia, CardContent, Typography, Skeleton, Alert, Container, Box, Button } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';

function CategoryPage() {
  const { category } = useParams();
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  const loadPhotos = useCallback(async () => {
    try {
      setError(null);
      setLoading(true);
      const response = await searchPhotos(category);
      setPhotos(response.data);
    } catch (error) {
      setError("Kategori fotoğrafları yüklenirken bir hata oluştu. Lütfen daha sonra tekrar deneyin.");
      console.error("Kategori fotoğrafları yüklenirken hata oluştu:", error);
    } finally {
      setLoading(false);
    }
  }, [category]);

  useEffect(() => {
    loadPhotos();
  }, [loadPhotos, refreshKey]);

  if (error) {
    return (
      <Container maxWidth="lg">
        <Alert severity="error" sx={{ mt: 4 }}>
          {error}
          <Button onClick={loadPhotos} sx={{ ml: 2 }}>
            <RefreshIcon sx={{ mr: 1 }} /> Tekrar Dene
          </Button>
        </Alert>
      </Container>
    );
  }

  const categoryTitle = category.charAt(0).toUpperCase() + category.slice(1) + " Kategorisi";

  return (
    <Container maxWidth="lg">
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4, mt: 2 }}>
        <Typography variant="h4" component="h1" fontWeight={700} color="text.primary">
          {categoryTitle}
        </Typography>
        <Button 
          variant="outlined" 
          startIcon={<RefreshIcon />}
          onClick={() => setRefreshKey(prev => prev + 1)}
          sx={{ borderRadius: 2 }}
        >
          Yeni Fotoğraflar
        </Button>
      </Box>
      <Grid container spacing={3}>
        {loading ? 
          Array.from(new Array(12)).map((_, i) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={`skeleton-${i}`}>
              <Skeleton variant="rectangular" height={200} sx={{ borderRadius: 1 }} />
              <Skeleton sx={{ mt: 1, height: 20 }} />
            </Grid>
          )) 
          : 
          photos.map(photo => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
              <Card sx={{ height: '100%' }}>
                <Link to={`/photo/${photo.id}`} style={{ textDecoration: 'none' }}>
                  <CardMedia
                    component="img"
                    height="200"
                    image={photo.thumbnailUrl}
                    alt={photo.title}
                  />
                </Link>
                <CardContent sx={{ p: 2 }}>
                  <Typography variant="body2" color="text.secondary" sx={{ height: 40, overflow: 'hidden' }}>
                    {photo.title || 'Açıklama yok'}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))
        }
      </Grid>
    </Container>
  );
}
