import React, { useEffect, useState } from 'react';
import { fetchRandomPhotos } from './api';
import { Grid, Card, CardMedia, CardContent, Typography } from '@mui/material';
import { Link } from 'react-router-dom';

function HomePage() {
  const [photos, setPhotos] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchRandomPhotos()
      .then(res => setPhotos(res.data))
      .catch(err => setError('Fotoğraflar yüklenirken hata oluştu'));
  }, []);

  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <Grid container spacing={2}>
      {photos.map(photo => (
        <Grid item xs={12} sm={6} md={4} key={photo.id}>
          <Card component={Link} to={`/photo/${photo.id}`} sx={{ textDecoration: 'none' }}>
            <CardMedia
              component="img"
              height="200"
              image={photo.urls?.thumb} // güvenli erişim
              alt={photo.alt_description || 'Fotoğraf'}
            />
            <CardContent>
              <Typography variant="body2">{photo.alt_description || 'Açıklama yok'}</Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default HomePage;
